// 이 파일은 Webpack에 의해 자동으로 컴파일됩니다. 실제 애플리케이션 로직은
// app/javascript 디렉토리 내의 관련 구조에 배치하고, 이 파일에서는 그 코드들을 참조하여 컴파일합니다.

require("@rails/ujs").start()
require("turbolinks").start()
require("@rails/activestorage").start()
require("channels")

import 'semantic-ui-css/semantic.min.css';
import $ from 'jquery';  // jQuery를 먼저 import
import 'semantic-ui-css/semantic.min.js';  // 그 후 Semantic UI를 불러옴

$(document).ready(function() {
  // Semantic UI dropdown 초기화
  $('.ui.dropdown').dropdown();

  // CSRF 토큰을 헤더에 추가하여 fetch 요청을 보내는 코드
  const token = document.querySelector('meta[name="csrf-token"]').getAttribute('content');

  // 예시로 카테고리를 선택하여 POST 요청 보내기
  $('#category-form').submit(function(event) {
    event.preventDefault();  // 폼 제출을 막고 AJAX로 처리

    const categoryValue = $('#category-select').val();  // 선택된 카테고리 값 가져오기

    fetch('/categories', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'X-CSRF-Token': token  // CSRF 토큰 추가
      },
      body: JSON.stringify({ category: categoryValue })  // 선택된 카테고리 데이터 전송
    })
    .then(response => response.json())
    .then(data => console.log('성공:', data))  // 성공 시 응답 처리
    .catch(error => console.error('오류:', error));  // 실패 시 오류 처리
  });
});

$(document).ready(function() {
  $('#profile-image').click(function() {
    $('#dropdown-menu').toggle(); // 드롭다운 토글
  });
});
